originalContent:
/* Utility Classes */
.text-grey {
    color: grey;
}

.text-deep-blue {
    color: #00008B; /* Deep Blue */
}

.font-large {
    font-size: 30px;
}

.font-medium {
    font-size: 20px;
}

.font-small {
    font-size: 16px;
}

.font-bold {
    font-weight: bold;
}

.font-italic {
    font-style: italic;
}

/* Dark Theme */
.dark-theme {
    background-color: #121212;
    color: white;
    font-family: Arial, sans-serif;
    padding: 20px;
}

/* Controls */
.controls {
    margin-bottom: 20px;
}

.controls label {
    margin-right: 10px;
}

.controls select {
    margin-right: 20px;
    padding: 5px;
    font-size: 16px;
}

/* Gallery Layout */
.gallery {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
}

/* Painting Container */
.painting {
    border: 2px solid #444;
    border-radius: 8px;
    padding: 15px;
    cursor: pointer;
    transition: transform 0.2s, box-shadow 0.2s;
    background-color: #1e1e1e;
}

.painting:focus {
    outline: 3px solid #555;
}

.painting:hover {
    transform: scale(1.02);
    box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
}

/* Painting Image */
.painting img {
    width: 100%;
    height: auto;
    border-radius: 4px;
    loading: lazy;
}

/* Title Style */
.title {
    font-size: 30px;
    color: #00008B; /* Deep Blue */
    font-weight: bold;
    margin: 10px 0 5px 0;
}

/* Artist Style */
.artist {
    font-size: 20px;
    color: black;
    font-style: italic;
    margin: 5px 0;
}

/* Description Style */
.description {
    font-size: 16px;
    color: grey;
    font-style: italic;
    margin: 5px 0 15px 0;
}

/* Special Note Style */
.special-note {
    color: #FFD700; /* Gold */
    font-weight: bold;
}

/* Modal Styles */
.modal {
    display: none; /* Hidden by default */
    position: fixed;
    z-index: 1000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgba(0,0,0,0.8);
    opacity: 0;
    transition: opacity 0.5s ease;
}

.modal.show {
    display: block;
    opacity: 1;
}

.modal-content {
    background-color: #f4f4f4;
    margin: 10% auto;
    padding: 20px;
    border: 1px solid #888;
    width: 80%;
    max-width: 700px;
    border-radius: 10px;
    position: relative;
    animation: fadeIn 0.5s;
}

@keyframes fadeIn {
    from { transform: translateY(-50px); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
}

.close {
    color: #aaa;
    position: absolute;
    top: 15px;
    right: 25px;
    font-size: 30px;
    font-weight: bold;
    cursor: pointer;
    transition: color 0.3s;
}

.close:hover,
.close:focus {
    color: black;
    text-decoration: none;
}

.modal-image {
    width: 100%;
    height: auto;
    border-radius: 4px;
}

/* Responsive Design */
@media (max-width: 600px) {
    .modal-content {
        width: 95%;
    }

    .controls {
        display: flex;
        flex-direction: column;
    }

    .controls select {
        margin-bottom: 10px;
    }
}

